DROP TABLE IF EXISTS dbo._tb_wordle_base_hist;

--final history table
CREATE TABLE dbo._tb_wordle_base_hist
(
ID INT IDENTITY(1,1) NOT NULL
,past_word VARCHAR(5) NOT NULL
,date_word DATE
,CONSTRAINT [PK_tb_wordle_base_hist] PRIMARY KEY CLUSTERED (ID)
);

INSERT INTO dbo._tb_wordle_base_hist
SELECT 
	past_word
	,NULL
FROM dbo._tb_wordle_past_words;

--add dates where given word was used
WITH CTE AS
(
SELECT 
	ID
	,past_word
	,ROW_NUMBER() OVER(ORDER BY ID DESC) as rn 
FROM dbo._tb_wordle_base_hist
)

UPDATE dbo._tb_wordle_base_hist
SET date_word =  DATEADD(day, rn, '2021-06-18') --2021-06-19 was the first day Wordle was launched, so we need the day before
FROM _tb_wordle_base_hist wbh
JOIN CTE c ON c.ID = wbh.ID;

CREATE NONCLUSTERED INDEX NC__tb_wordle_base_hist__past_word ON _tb_wordle_base_hist(past_word);

